var util = require('cenfo-rsa');
module.exports = {

  validate_encrypt: function (decryptText) {

    var  encryptText ='';
    try {
     encrypt = util.encriptar(decryptText);
    } catch (e) {
      console.log(e);
    } finally {
        return encrypt;
    }
  },
  validate_decrypt: function (encryptText)
  {
    var  decryptText ='';
        try {
         decryptText = util.desencriptar(encryptText);
        } catch (e) {
          console.log(e);
        } finally {
            return decryptText;
        }
  }
};
